"use strict";exports.formatDate=t=>{const r=new Date(t);return`${r.getFullYear()}-${String(r.getMonth()+1).padStart(2,"0")}-${String(r.getDate()).padStart(2,"0")}`},exports.formatDuration=t=>{if(!t)return"0";const r=t%60;return`${Math.floor(t/3600)}:${Math.floor(t%3600/60).toString().padStart(2,"0")}:${r.toString().padStart(2,"0")}`},exports.formatPace=t=>{if(!t)return"0";return`${Math.floor(t/60)}'${Math.floor(t%60).toString().padStart(2,"0")}"/100m`};
